<?php

namespace Tests\Feature\Book;

use App\Models\Book;
use App\Models\BookIndex;
use App\Models\User;
use Illuminate\Foundation\Testing\RefreshDatabase;
use Illuminate\Foundation\Testing\WithFaker;
use Laravel\Sanctum\Sanctum;
use Tests\TestCase;

class UpdateBookTest extends TestCase
{
    use RefreshDatabase;
    /**
     * A basic feature test example.
     *
     * @return void
     */
    public function test_to_update_an_book()
    {
        $user = User::factory()->create();
        $book = Book::factory([
            'publisher_user_id' => $user->id
        ])->create();
        $bookIndexFactory = BookIndex::factory([
            'book_id' => $book->id,
            'index_id' => null,
        ])->count(10)->create();

        Sanctum::actingAs($user);

        $response = $this->putJson('/api/v1/livros/' . $book->id, [
            "titulo " => "exemplo",
            "indices" => [
                [
                    "titulo " => "indice 1",
                    "pagina" => 2,
                    "subindices" => [
                        [
                            "titulo " => "indice 1.1",
                            "pagina" => 3,
                            "subindices" => []
                        ]
                    ]
                ],
                [
                    "titulo " => "indice 2",
                    "pagina" => 4,
                    "subindices" => []
                ]
            ],
            "document" => ""


        ]);
        $response->assertStatus(200);
    }

    public function test_to_update_an_book_with_invalid_args()
    {
        $user = User::factory()->create();
        $book = Book::factory([
            'publisher_user_id' => $user->id
        ])->create();
        $bookIndexFactory = BookIndex::factory([
            'book_id' => $book->id,
            'index_id' => null,
        ])->count(10)->create();

        Sanctum::actingAs($user);

        $response = $this->putJson('/api/v1/livros/' . $book->id, [
            "titulosss " => "exemplo",
            "indicessss" => [
                [
                    "titulo " => "indice 1",
                    "pagina" => 2,
                    "subindices" => [
                        [
                            "titulo " => "indice 1.1",
                            "pagina" => 3,
                            "subindices" => []
                        ]
                    ]
                ],
                [
                    "titulo " => "indice 2",
                    "pagina" => 4,
                    "subindices" => []
                ]
            ],
            "documentsss" => ""


        ]);
        $response->assertStatus(422);
    }

    public function test_to_update_an_book_without_user_logged_id(){
        $user = User::factory()->create();
        $book = Book::factory([
            'publisher_user_id' => $user->id
        ])->create();
        $bookIndexFactory = BookIndex::factory([
            'book_id' => $book->id,
            'index_id' => null,
        ])->count(10)->create();


        $response = $this->putJson('/api/v1/livros/' . $book->id, [
            "titulasdao " => "exemplo",
            "indasdaices" => [
                [
                    "titulo " => "indice 1",
                    "pagina" => 2,
                    "subindices" => [
                        [
                            "titulo " => "indice 1.1",
                            "pagina" => 3,
                            "subindices" => []
                        ]
                    ]
                ],
                [
                    "titulo " => "indice 2",
                    "pagina" => 4,
                    "subindices" => []
                ]
            ],
            "documasdent" => ""


        ]);
        $response->assertUnauthorized();
    }
}
